<script>import { theme } from "@latitude-data/client";
export let align = "left";
export let as = "span";
export let capitalize = false;
export let centered = false;
export let color = "foreground";
export let ellipsis = false;
export let leading = "h4";
export let lineThrough = false;
export let size = "h4";
export let spacing = "normal";
export let underline = false;
export let uppercase = false;
export let weight = "normal";
export let whiteSpace = "normal";
export let wordBreak = "normal";
let className = void 0;
export { className as class };
$:
  classes = theme.ui.text.cssClass({
    align,
    capitalize,
    centered,
    color,
    ellipsis,
    lineThrough,
    spacing,
    size,
    leading,
    underline,
    uppercase,
    weight,
    whiteSpace,
    wordBreak,
    class: className
  });
</script>

<svelte:element this={as} class={classes}>
